from bs4 import BeautifulSoup

# HTML-код таблиці
html_content = '''<table><thead><tr><th><strong>Command</strong></th><th><strong>Brief Description</strong></th><th><strong>Command Type</strong></th><th><strong>Permissions (if applicable)</strong></th><th><strong>Usage</strong></th></tr></thead><tbody><tr><td><code>ls</code></td><td>Lists files and directories in the current directory or specified path.</td><td>Binary</td><td>No special permissions</td><td>Short usage: <code>ls</code></td></tr><tr><td><code>cd</code></td><td>Changes the current directory.</td><td>Shell built-in</td><td>No special permissions</td><td>Short usage: <code>cd path</code></td></tr><tr><td><code>pwd</code></td><td>Prints the current working directory.</td><td>Shell built-in</td><td>No special permissions</td><td>Short usage: <code>pwd</code></td></tr><tr><td><code>cp</code></td><td>Copies files or directories from one location to another.</td><td>Binary</td><td>Requires read permissions for source, write permissions for destination</td><td>Short usage: <code>cp src dest</code></td></tr><tr><td><code>mv</code></td><td>Moves or renames files or directories.</td><td>Binary</td><td>Requires write permissions on source and destination</td><td>Short usage: <code>mv src dest</code></td></tr><tr><td><code>rm</code></td><td>Removes files or directories.</td><td>Binary</td><td>Requires write permissions on files or directories</td><td>Short usage: <code>rm file</code></td></tr><tr><td><code>touch</code></td><td>Creates a new empty file or updates the timestamp of an existing file.</td><td>Binary</td><td>Requires write permissions in the directory</td><td>Short usage: <code>touch file</code></td></tr><tr><td><code>cat</code></td><td>Concatenates and displays the content of files.</td><td>Binary</td><td>Requires read permissions on files</td><td>Short usage: <code>cat file</code></td></tr><tr><td><code>echo</code></td><td>Displays a line of text or variable value.</td><td>Shell built-in</td><td>No special permissions</td><td>Short usage: <code>echo text</code></td></tr><tr><td><code>man</code></td><td>Displays the manual or help page for a command.</td><td>Binary</td><td>No special permissions</td><td>Traditional usage: <code>man command</code></td></tr><tr><td><code>chmod</code></td><td>Changes the permissions of a file or directory.</td><td>Binary</td><td>Requires write permissions on the file/directory</td><td>Traditional usage: <code>chmod 755 file</code></td></tr><tr><td><code>chown</code></td><td>Changes the owner and/or group of a file or directory.</td><td>Binary</td><td>Requires root or sudo permissions</td><td>Traditional usage: <code>chown user:group file</code></td></tr><tr><td><code>ps</code></td><td>Displays information about running processes.</td><td>Binary</td><td>No special permissions</td><td>Short usage: <code>ps</code></td></tr><tr><td><code>top</code></td><td>Displays real-time system and process information.</td><td>Binary</td><td>No special permissions</td><td>Short usage: <code>top</code></td></tr><tr><td><code>kill</code></td><td>Sends a signal to terminate a process.</td><td>Binary</td><td>Requires permissions on the target process</td><td>Short usage: <code>kill PID</code></td></tr><tr><td><code>df</code></td><td>Shows disk space usage for mounted file systems.</td><td>Binary</td><td>No special permissions</td><td>Short usage: <code>df</code></td></tr><tr><td><code>du</code></td><td>Displays disk usage of files and directories.</td><td>Binary</td><td>No special permissions</td><td>Short usage: <code>du</code></td></tr><tr><td><code>grep</code></td><td>Searches for patterns in a file or input.</td><td>Binary</td><td>Requires read permissions on the file</td><td>Short usage: <code>grep pattern file</code></td></tr><tr><td><code>find</code></td><td>Searches for files and directories in a specified location based on conditions.</td><td>Binary</td><td>Requires read permissions in the directories</td><td>Short usage: <code>find path -name filename</code></td></tr><tr><td><code>sudo</code></td><td>Executes a command with superuser privileges.</td><td>Binary</td><td>Requires user to be in sudoers file</td><td>Short usage: <code>sudo command</code></td></tr><tr><td><code>su</code></td><td>Switches to another user (default is root).</td><td>Binary</td><td>Requires root password or permissions</td><td>Traditional usage: <code>su</code></td></tr><tr><td><code>whoami</code></td><td>Displays the currently logged-in user.</td><td>Binary</td><td>No special permissions</td><td>Short usage: <code>whoami</code></td></tr><tr><td><code>uptime</code></td><td>Displays the system uptime, load average, and number of users.</td><td>Binary</td><td>No special permissions</td><td>Short usage: <code>uptime</code></td></tr><tr><td><code>alias</code></td><td>Creates an alias for a command or series of commands.</td><td>Shell built-in</td><td>No special permissions</td><td>Short usage: <code>alias name='command'</code></td></tr><tr><td><code>history</code></td><td>Displays a list of recently executed commands.</td><td>Shell built-in</td><td>No special permissions</td><td>Short usage: <code>history</code></td></tr><tr><td><code>wget</code></td><td>Downloads files from the web using HTTP, HTTPS, or FTP protocols.</td><td>Binary</td><td>Requires write permissions in the destination directory</td><td>Short usage: <code>wget URL</code></td></tr><tr><td><code>curl</code></td><td>Transfers data from or to a server using different protocols (HTTP, FTP, etc.).</td><td>Binary</td><td>Requires write permissions in the destination directory</td><td>Short usage: <code>curl URL</code></td></tr><tr><td><code>nano</code></td><td>A simple text editor.</td><td>Binary</td><td>Requires write permissions on the file</td><td>Short usage: <code>nano file</code></td></tr><tr><td><code>vim</code></td><td>A more advanced text editor.</td><td>Binary</td><td>Requires write permissions on the file</td><td>Traditional usage: <code>vim file</code></td></tr></tbody></table>'''

soup = BeautifulSoup(html_content, 'html.parser')

rows = soup.find_all('tr')

with open("hm3/commands.txt", "w") as file:

    for row in rows:
        columns = row.find_all(['th', 'td'])
        # Перетворюємо кожен стовпець в текст і об'єднуємо їх через ';'
        data = [col.get_text(strip=True) for col in columns]
        # Записуємо в файл, додаючи крапку з комою між стовпцями
        file.write(';'.join(data) + '\n')

